// 1️⃣ — Состояние экрана
data class $NAME$UiState(
    val message: String = "",
    val isSending: Boolean = false,
    val error: String? = null,
    val successMessage: String? = null
)

// 2️⃣ — ViewModel
class $NAME$ViewModel : ViewModel() {

    private val _uiState = MutableStateFlow($NAME$UiState())
    val uiState: StateFlow<$NAME$UiState> = _uiState

    fun onMessageChange(newMessage: String) {
        _uiState.update { it.copy(message = newMessage, error = null) }
    }

    fun onSendClick(context: Context) {
        val msg = _uiState.value.message.trim()
        if (msg.isBlank()) {
            _uiState.update { it.copy(error = "Сообщение не может быть пустым") }
            return
        }

        viewModelScope.launch {
            _uiState.update { it.copy(isSending = true) }
            try {
                delay(3000)

//                val intent = Intent(Intent.ACTION_SENDTO).apply {
//                    data = Uri.parse("mailto:")
//                    putExtra(Intent.EXTRA_EMAIL, arrayOf("support@yourapp.com"))
//                    putExtra(Intent.EXTRA_SUBJECT, "Обратная связь")
//                    putExtra(Intent.EXTRA_TEXT, msg)
//                }
//                intent.addFlags(Intent.FLAG_ACTIVITY_NEW_TASK)
//                context.startActivity(intent)

                _uiState.update {
                    it.copy(
                        isSending = false,
                        successMessage = "Спасибо за сообщение!",
                        message = ""
                    )
                }

            } catch (e: Exception) {
                _uiState.update {
                    it.copy(isSending = false, error = "Не удалось открыть почтовое приложение")
                }
            }
        }
    }

    fun clearSuccess() {
        _uiState.update { it.copy(successMessage = null) }
    }
}

// 3️⃣ — Контейнерный компонент
@Composable
fun $NAME$Screen(
    viewModel: $NAME$ViewModel = koinViewModel(),
    on$NAME$Sent: () -> Unit
) {
    val uiState by viewModel.uiState.collectAsState()
    val context = LocalContext.current

    $NAME$ScreenBody(
        state = uiState,
        onMessageChange = viewModel::onMessageChange,
        onSendClick = {
            viewModel.onSendClick(context)
        },
        onBack = {}
    )

    LaunchedEffect(uiState.successMessage) {
        uiState.successMessage?.let {
            Toast.makeText(context, it, Toast.LENGTH_LONG).show()
            //viewModel.clearState()
            on$NAME$Sent()
        }
    }
}

// 4️⃣ — UI-компонент (только данные и колбэки)
@OptIn(ExperimentalMaterial3Api::class)
@Composable
fun $NAME$ScreenBody(
    state: $NAME$UiState,
    onBack: () -> Unit,
    onMessageChange: (String) -> Unit,
    onSendClick: () -> Unit
) {
    Scaffold(
        topBar = {
            TopAppBar(
                title = { Text("Обратная связь") },
                navigationIcon = {
                    IconButton(onClick = onBack) {
                        Icon(
                            imageVector = Icons.AutoMirrored.Outlined.ArrowBack,
                            contentDescription = "Назад"
                        )
                    }
                }
            )
        },
        floatingActionButton = {
            FloatingActionButton(
                onClick = {
                    if (!state.isSending) onSendClick()
                },
                containerColor = if (state.isSending)
                    MaterialTheme.colorScheme.surfaceVariant else FloatingActionButtonDefaults.containerColor,
                elevation = FloatingActionButtonDefaults.elevation()
            ) {
                if (state.isSending) {
                    CircularProgressIndicator(
                        modifier = Modifier.size(20.dp),
                        color = MaterialTheme.colorScheme.onSurfaceVariant,
                        strokeWidth = 2.dp
                    )
                } else {
                    //Icons.Filled.Forum
                    //Icons.Filled.Email
                    Icon(Icons.AutoMirrored.Filled.Send, contentDescription = "Отправить")
                }
            }
        }
    ) { padding ->
        Column(
            modifier = Modifier
                .padding(padding)
                .padding(16.dp)
                .fillMaxSize()
        ) {
            OutlinedTextField(
                value = state.message,
                onValueChange = onMessageChange,
                label = { Text("Ваши пожелания") },
                placeholder = { Text("Ваше предложение, проблема или отзыв") },
                modifier = Modifier
                    .fillMaxWidth()
                    .heightIn(min = 180.dp),
                maxLines = 10,
                singleLine = false
            )

            if (state.error != null) {
                Text(
                    text = state.error,
                    color = MaterialTheme.colorScheme.error,
                    style = MaterialTheme.typography.bodySmall,
                    modifier = Modifier.padding(top = 8.dp)
                )
            }
        }
    }
}



