@Composable
fun $SCREEN_NAME$Screen(
    gridColumns: Int = 4,
    onItemClick: ($ITEM_TYPE$) -> Unit,
    onScroll: (isTopBarVisible: Boolean) -> Unit,
    $VIEWMODEL_VAR$: $VIEWMODEL_CLASS$ = koinViewModel($VIEWMODEL_CLASS$::class)
) {
    val uiState by $VIEWMODEL_VAR$.uiState.collectAsState()
    val orientation = LocalConfiguration.current.orientation

    when (orientation) {
        Configuration.ORIENTATION_PORTRAIT -> $SCREEN_NAME$Portrait(uiState, gridColumns, onItemClick, onScroll)
        Configuration.ORIENTATION_LANDSCAPE -> $SCREEN_NAME$Landscape(uiState, gridColumns, onItemClick, onScroll)
        else -> $SCREEN_NAME$Portrait(uiState, gridColumns, onItemClick, onScroll)
    }
}

@Composable
fun $SCREEN_NAME$Portrait(
    uiState: $UI_STATE_CLASS$,
    gridColumns: Int,
    onItemClick: ($ITEM_TYPE$) -> Unit,
    onScroll: (Boolean) -> Unit
) {
    when (uiState) {
        $UI_STATE_CLASS$.Loading -> {
            // TODO: Показать лоадер
        }
        is $UI_STATE_CLASS$.Ready -> {
            $SCREEN_NAME$Content(
                gridColumns = gridColumns,
                items = uiState.$DATA_FIELD$,
                onItemClick = onItemClick,
                onScroll = onScroll
            )
        }
    }
}

