                        BackHandler(drawerState.isOpen || isFullScreen != FullScreen.None) {
                            if (isFullScreen != FullScreen.None) {
                                isFullScreen = FullScreen.None
                            } else if (drawerState.isOpen) {
                                scope.launch {
