sealed class UiState {
    object Idle : UiState()
    object Loading : UiState()
    data class Success(val data: String) : UiState()
    data class Error(val message: String) : UiState()
}

class $CLASS_NAME$ViewModel : ViewModel() {

    private val _uiState = MutableStateFlow<UiState>(UiState.Idle)
    val uiState: StateFlow<UiState> = _uiState

    fun onButtonClick() {
        _uiState.value = UiState.Loading

        // Симуляция загрузки данных
        viewModelScope.launch {
            delay(1500) // имитация сети
            if (Random.nextBoolean()) {
                _uiState.value = UiState.Success("Data loaded successfully!")
            } else {
                _uiState.value = UiState.Error("Something went wrong!")
            }
        }
    }
}
@Composable
fun $CLASS_NAME$Screen($CLASS_NAME$ViewModel: $CLASS_NAME$ViewModel = viewModel()) {
    val context = LocalContext.current
    val uiState by $CLASS_NAME$ViewModel.uiState.collectAsState()

    Box(
        modifier = Modifier
            .fillMaxSize()
            .padding(16.dp),
        contentAlignment = Alignment.Center
    ) {
        when (uiState) {
            is UiState.Idle -> {
                Button(onClick = { $CLASS_NAME$ViewModel.onButtonClick() }) {
                    Text("Load Data")
                }
            }
            is UiState.Loading -> {
                CircularProgressIndicator()
            }
            is UiState.Success -> {
                Column(horizontalAlignment = Alignment.CenterHorizontally) {
                    Text((uiState as UiState.Success).data)
                    Spacer(modifier = Modifier.height(16.dp))
                    Button(onClick = { $CLASS_NAME$ViewModel.onButtonClick() }) {
                        Text("Reload")
                    }
                }
            }
            is UiState.Error -> {
                Column(horizontalAlignment = Alignment.CenterHorizontally) {
                    Text((uiState as UiState.Error).message, color = MaterialTheme.colorScheme.error)
                    Spacer(modifier = Modifier.height(16.dp))
                    Button(onClick = { 
                        $CLASS_NAME$ViewModel.onButtonClick()
                        Toast.makeText(context, "Retry clicked", Toast.LENGTH_SHORT).show()
                    }) {
                        Text("Retry")
                    }
                }
            }
        }
    }
}
